export function touched(node, { field = null, callback = null, touched = 'touched' } = {}) {
    const focusHandler = (e) => {
        if (!node.classList.contains(touched)) {
            node.classList.add(touched);
            field._set('touched', true);
        }
    };
    node.addEventListener('focus', focusHandler);
    return {
        destroy() {
            node.removeEventListener('focus', focusHandler);
        }
    };
}
